SELECT Mst_Course."Name" AS "Course", Mst_Class."Name" AS "Class", E_Days."Name" AS "Day", E_Class_Status."Name" AS "Status", Mst_Faculty."Name" AS "Faculty", Mst_Faculty."Email" AS "Email", Mst_Faculty."Phone" AS "Phone", "Start", "End" FROM Trn_Schedule JOIN E_Class_Status ON Trn_Schedule."Status"=E_Class_Status.Id JOIN E_Days ON Trn_Schedule."Day"=E_Days.Id JOIN Mst_Course ON Trn_Schedule."Course"=Mst_Course.Id JOIN Mst_Class ON Trn_Schedule."Class"=Mst_Class."Id" JOIN Mst_Faculty ON Trn_Schedule."Faculty"=Mst_Faculty."Id" ORDER BY Trn_Schedule."Start" ASC