SELECT Mst_Course."Name" AS "Course", COUNT(Trn_Schedule."Id") AS Cnt FROM Trn_Schedule JOIN Mst_Course ON Trn_Schedule."Course"=Mst_Course.Id GROUP BY Mst_Course."Name";

SELECT Mst_Course."Name" AS "Course", COUNT(Map_Trn_Schedule_Student_Attendance."Id") AS Cnt FROM Trn_Schedule JOIN Mst_Course ON Trn_Schedule."Course"=Mst_Course.Id LEFT JOIN Map_Trn_Schedule_Student_Attendance ON Map_Trn_Schedule_Student_Attendance."Schedule" = Trn_Schedule."Id" AND Map_Trn_Schedule_Student_Attendance."Student"=3 LEFT JOIN E_Attendance ON Map_Trn_Schedule_Student_Attendance."Attendance" = E_Attendance."Id" WHERE Map_Trn_Schedule_Student_Attendance."Attendance"=1 GROUP BY Mst_Course."Name";
SELECT Mst_Course."Name" AS "Course", COUNT(Map_Trn_Schedule_Student_Attendance."Id") AS Cnt FROM Trn_Schedule JOIN Mst_Course ON Trn_Schedule."Course"=Mst_Course.Id LEFT JOIN Map_Trn_Schedule_Student_Attendance ON Map_Trn_Schedule_Student_Attendance."Schedule" = Trn_Schedule."Id" AND Map_Trn_Schedule_Student_Attendance."Student"=3 LEFT JOIN E_Attendance ON Map_Trn_Schedule_Student_Attendance."Attendance" = E_Attendance."Id" GROUP BY Mst_Course."Name";